mapper.sql:

    AND u.user_id = #{userId}


    AND u.user_name like concat('%', #{userName}, '%')


    AND u.status = #{status}


    AND u.phonenumber like concat('%', #{phoneNum}, '%')

/* todo 这是在查询什么*/

    AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))

mapper.select:
select u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_id, d.parent_id, d.dept_name, d.order_num, d.leader, d.status as dept_status, r.role_id, r.role_name, r.role_key, r.role_sort, r.data_scope, r.status as role_status from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id
AND u.user_id = #{userId}
AND u.user_name = #{userName}
